sys_script_include.script:
/* eslint-disable no-undef */
/* eslint-disable no-unused-vars */
/**
 * @class TranslationDecoder
 * @description Converts an  characterSet into its original character.
 * @since 09/11/2023
 * @date  09/11/2023
 * @version 1.0.0
 * @author Thomas Grensemann E-Mail: <thomas.grensemann@t-systems.com>
 *
 */
class TranslationDecoder {

    /**
     * Converts an  characterSet into its original character.
     *
     * @param {String} characterSet represents a set of characters
     *
     * @returns {String} string from a sequence of Unicode values.
     **/
    static entityDecode(characterSet) {
        const regex = /&#(\d+);/;
        // eslint-disable-next-line func-names, prefer-arrow-callback
        return characterSet.replace(regex, function(dec) {
            const limit = 1;
            const exec = regex.exec(dec);
            return String.fromCharCode(exec[limit]);
        });
    }

    /**
     * Decodes a string to its html characters completely.
     *
     * @param {String} string String with unescaped HTML characters
     * @returns {String}
     **/
    static namedEntityDecode(string) {
        // eslint-disable-next-line func-names
        return string.replace(/(&\w+;)/g, (index) => {
            return Constants.NAMED_ENTITIES[index] || '';
        });
    }

    /**
     *
     * Strip the HTML tags from a translated HTML message
     *
     * @param {Object} translationObj translation object see class TranslationObject
     * @param {String} _translation html message
     *
     * @returns {String}  message without html tags
     **/
    static stripHtml(translationObj, _translation) {
        let translation = _translation;
        if (translationObj.toStrip()) {
            translation = UtilityFunctions.stripHtmlTags(translation);
        }

        return translation;
    }
}
