/**
 * @class EnabledCondition
 * @description Validates if a email has to be send for a given ticket.
 * @since 29/04/2024
 * @date  29/04/2024
 * @version 1.0.0
 * @author Thomas Grensemann E-Mail: <thomas.grensemann@t-systems.com>
 *
 */
class EnabledCondition extends ConditionController {

    /**
   * Validates if a email has to be send for a given ticket.
   *
   * @param {object} conf see NotificationConditionController#notificationEnabled()
   *
   * @returns {Boolean} true/false
    */
    checkCondition(conf) {
        let hasToNotified = true;

        let sysEventEmailAction = x_dtitg_core_smnow.GlideRecordUtils.getRecord(Constants.NOTIFICATION, 'name', conf.name);

        gs.debug(`[EnabledCondition][checkCondition] sysEventEmailAction => ${ sysEventEmailAction}`);

        let hasEntry = this._hasEnabledEntries(sysEventEmailAction);

        gs.debug(`[EnabledCondition][checkCondition] hasEntry => ${ hasEntry}`);

        if (hasEntry) {
            let processor = new CompanyDeterminerProcessor();
            let company = processor.findCompany(conf.current, sysEventEmailAction, conf.event);

            gs.debug(`[EnabledCondition][checkCondition] company => ${ company}`);

            processor = new BusinessServiceDeterminerProcessor();
            let businessService = processor.findBusinessService(conf.current, sysEventEmailAction, conf.event);

            gs.debug(`[EnabledCondition][checkCondition] businessService => ${ businessService}`);

            processor = new AssignmentGroupDeterminerProcessor();
            let assignmentGroup = processor.findAssignmentGroup(conf.current, sysEventEmailAction, conf.event);

            gs.debug(`[EnabledCondition][checkCondition] assignmentGroup => ${ assignmentGroup}`);

            hasToNotified = this._hasToNotified(company, businessService, assignmentGroup, sysEventEmailAction);
        }

        gs.debug(`[EnabledCondition][checkCondition] hasToNotified => ${ hasToNotified}`);

        return hasToNotified;
    }

    /**
   * Checks whether the table "Enabled (x_dtitg_notificati_enabled)" contains an entry.
   *
   * @param {Object} sysEventEmailAction sysevent_email_action GlideRecord object
   * @param {String} assignmentGroup sys_id of the assignment group
   *
   * @returns {Boolean} true/false
   */
    _hasEnabledEntries(sysEventEmailAction) {
        let query = QueryBuilder.findEntries('notification', sysEventEmailAction.getUniqueValue());
        gs.debug(`[EnabledCondition][_hasEnabledEntries] query => ${ query}`);

        return x_dtitg_core_smnow.GlideRecordUtils.hasEntry(Constants.ENABLED, query);
    }


    /**
       * Checks if the table "Enabled (x_dtitg_notificati_enabled)" contains an entry for the given paramters.
       *
       * @param {String} company sys_id
       * @param {String} businessService sys_id
       * @param {String} assignmentGroup sys_id
       * @param {Object} sysEventEmailAction sysevent_email_action GlideRecord object
       *
       * @returns {Boolean} true/false
       */
    _hasToNotified(company, businessService, assignmentGroup, sysEventEmailAction) {

        let query = QueryBuilder.findEnabledEntrie(
            sysEventEmailAction.getUniqueValue(),
            company,
            businessService,
            assignmentGroup
        );

        gs.debug(`[EnabledCondition][_hasToNotified] query => ${ query}`);

        return x_dtitg_core_smnow.GlideRecordUtils.hasEntry(Constants.ENABLED, query);
    }
}
