/* eslint-disable no-undef */
/* eslint-disable no-unused-vars */
/**
 * @class ChangeTicketCompany
 * @description Determines the company for an change ticket.
 * @since 09/11/2023
 * @date  09/11/2023
 * @version 1.0.0
 * @author Thomas Grensemann E-Mail: <thomas.grensemann@t-systems.com>
 *
 */
class ChangeTicketCompany extends CompanyDeterminer {

    /**
     * Determines the company for an change ticket
     *
     * @param {Object} current change ticket record
     * @param {Object} sysEventEmailAction sysevent_email_action record
     * @param {Object} event event that triggers the notificatoin
     *
     * @returns {String} company sys_id
     */
    determineCompany(current, sysEventEmailAction, event) {
        let company = '';

        try {
            if (current.getTableName() === Constants.CHANGE) {
                company = current.requested_by.company.toString();
            }
        } catch (ex) {
            throw new CompanyNotFoundException(`[ChangeTicketCompany][determineCompany] ... ==> ${ ex.message}`);
        }

        gs.debug(`[ChangeTicketCompany][determineCompany] company => ${ company}`);

        return company;
    }
}
