/**
 * @class LinkGenerator
 * @description Base script for the Chain of Responsibility to determine the URL of a ticket..
 * @since 11/3/2024
 * @date  11/3/2024
 * @version 1.0.0
 * @author Siddhant Salunkhe E-Mail: <siddhant.salunkhe@t-systems.com>
 *
 */

class LinkGenerator {

    /**
     * Client passes a chain object. This improves flexibility and
     * makes testing  easier.
     *
     * @param {Object} _nextProcessor chain object
     */
    setNext(_nextProcessor) {
        this.nextProcessor = _nextProcessor;
    }

    /**
     * Runs check on the next object in chain or ends traversing if we're in
     * last object in chain.
     *
     * @param {Object} notifParams see email.engine.js
     * @param {Object} currObj - Current GlideRecord Object
     *
     * @returns{String} exact URL of ticket
     */
    process(notifParams, currObj) {
        let link = this.createTicketURL(notifParams, currObj);
        if (x_dtitg_core_smnow.UtilityFunctions.isBlank(link)) {
            if (this.nextProcessor) {
                link = this.nextProcessor.process(notifParams, currObj);
            }
        }

        return link;
    }

    /**
     * @description Astract function. Has to be implemted in child scripts.
     *
     * @param {Object} notifParams see email.engine.js
     * @param {Object} currObj - Current GlideRecord Object
     *
     *
     */
    createTicketURL(notifParams, currObj) {
        throw new Error('Abstract method!');
    }
}
