sys_script_include.script:
/* eslint-disable no-undef */
/* eslint-disable no-unused-vars */
/**
 * @class IncidentTicketAssignmentGroup
 * @description Determines the assignment group for an incident ticket
 * @since 13/06/2024
 * @date  13/06/2024
 * @version 1.0.0
 * @author Thomas Grensemann E-Mail: <thomas.grensemann@t-systems.com>
 *
 */
class IncidentTicketAssignmentGroup extends AssignmentGroupDeterminer {

    /**
    * Determines the assignment group for an incident ticket
    *
    * @param {Object} current  ticket record
    * @param {Object} sysEventEmailAction sysevent_email_action record
    * @param {Object} event event that triggers the notification
    *
    * @returns {String} assignment group sys_id of the assignment group
    */
    determineAssignmentGroup(current, sysEventEmailAction, event) {
        let assignmentGroup = '';

        if (current.getTableName() === Constants.INCIDENT) {

            gs.debug(`[TicketAssignmentGroup][determineAssignmentGroup] assignmentGroup => 
                ${ current.x_dtitg_itsm_smnow_opened_group}`);

            assignmentGroup = current.x_dtitg_itsm_smnow_opened_group.parent ?
                current.x_dtitg_itsm_smnow_opened_group.parent.toString() : current.x_dtitg_itsm_smnow_opened_group.toString();
        }

        gs.debug(`[TicketAssignmentGroup][determineAssignmentGroup] parent group => ${ assignmentGroup}`);

        return assignmentGroup;
    }
}
