/* eslint-disable no-unused-vars */
/* eslint-disable no-undef */
/**
 * @class TicketAssignmentGroup
 * @description Determines the assignment group for an event ticket
 * @since 29/04/2024
 * @date  29/04/2024
 * @version 1.0.0
 * @author Thomas Grensemann E-Mail: <thomas.grensemann@t-systems.com>
 *
 */
class TicketAssignmentGroup extends AssignmentGroupDeterminer {


    /**
     * Determines the assignment group for a ticket
     *
     * @param {Object} current  ticket record
     * @param {Object} sysEventEmailAction sysevent_email_action record
     * @param {Object} event event that triggers the notification
     *
     * @returns {String} assignment group sys_id of the assignment group
     */
    determineAssignmentGroup(current, sysEventEmailAction, event) {
        let assignmentGroup = '';
        if (current.assignment_group) {

            gs.debug(`[TicketAssignmentGroup][determineAssignmentGroup] assignmentGroup => ${ current.assignment_group}`);

            assignmentGroup = current.assignment_group.parent ?
                current.assignment_group.parent.toString() : current.assignment_group.toString();
        }

        gs.debug(`[TicketAssignmentGroup][determineAssignmentGroup] parent group => ${ assignmentGroup}`);

        return assignmentGroup;
    }

}
