sys_script_include.script:
/* eslint-disable no-undef */
/* eslint-disable no-unused-vars */
/**
 * @class TemplateData
 * @description Adds a TableRecord object to the tableRecord array.
 * @since 09/11/2023
 * @date  09/11/2023
 * @version 1.0.0
 * @author Thomas Grensemann E-Mail: <thomas.grensemann@t-systems.com>
 *
 */
class TemplateData {

    /**
     * Constructor
     */
    constructor() {
        this.tableRecords = [];
    }

    /**
     * Adds a TableRecord object to the tableRecord array.
     *
     * @param {Object} _tableRecord see TableRecord.js
     */
    addRecord(_tableRecord) {
        this.tableRecords.push(_tableRecord);
    }

    /**
     * Returns a table record
     *
     * @param {Number} index array index
     *
     * @returns {Object} table Record of the specific index
     */
    getTableRecord(index) {
        return this.tableRecords[index];
    }


    /**
     * Returns the table records
     *
     * @returns {Array} tableRecord
     */
    getTableRecords() {
        return this.tableRecords;
    }

    /**
     * Checks if tableRecord is empty
     *
     * @returns {Boolean} true/false
     */
    isEmpty() {
        return UtilityFunctions.isArrayEmpty(this.tableRecords);
    }

    /**
     * Returns an JSON string representation of the object
     * @returns {String} JSON string
     */
    toString() {
        return JSON.stringify(this);
    }
}
