sys_script_include.script:
/**
 * @class AppInstallDeactivateNotifcations
 * @description Dectivates OOTB Notifications
 * @since 21/06/2024
 * @date  21/06/2024
 * @version 1.0.0
 * @author Thomas Grensemann E-Mail: <thomas.grensemann@t-systems.com>
 *
 */
class AppInstallDeactivateNotifcations {


    deactivateIncidentNotifications (installer) {
        // SMNOW-660 Enabler: Setup Notifications (disable ootb incident notifications)
        var grNotifications = new GlideRecord('sysevent_email_action');
        grNotifications.addEncodedQuery('sys_scope=global^collection=incident^active=true');
        grNotifications.query();
        while (grNotifications.next()) {
            installer.RecordSetValue('Deactivate notification', 'sysevent_email_action', grNotifications.sys_id.toString(), 'active', false);
        }
    }


    deactivateProblemNotifications (installer) {
        // SMNOW-1637 Enabler: Feature: Email Notifaction (disable ootb problem / problem_task notifications)
        var grNotifications = new GlideRecord('sysevent_email_action');
        grNotifications.addEncodedQuery('sys_scope=global^collection=problem^ORcollection=problem_task^active=true');
        grNotifications.query();
        while (grNotifications.next()) {
            installer.RecordSetValue('Deactivate notification', 'sysevent_email_action', grNotifications.sys_id.toString(), 'active', false);
        }
    }

}
