sys_script_include.script:
/**
 * @description This script installs Notification SM.now.
 * @since 09/08/2023
 * @date  09/12/2023
 * @version 1.0.0
 * @author Thomas Grensemann E-Mail: <thomas.grensemann@t-systems.com>
 *
 */
function AppInstall() {
    var installer = new x_snc_devtools.InstallerAPI(x_snc_devtools.GetAppName('x_dtitg_notificati'));

    new x_dtitg_notificati.AppInstallSetEmailScript().installScript(installer);
    new x_dtitg_notificati.AppInstallDeactivateNotifcations().deactivateIncidentNotifications(installer);
    new x_dtitg_notificati.AppInstallDeactivateNotifcations().deactivateProblemNotifications(installer);

    installer.SetProperty('sn_ex_emp_fd.ex.email.redirection.portal.sysId', '89ba24fa2bcfe510c0fb4cfbad01a070');

    //[SMNOW-1281] - Capturing configurations for Incident commented notifications
    installer.RecordTransporterPackageUnpack('x_dtitg_notificati.notif_configuration');
    installer.RecordTransporterPackageUnpack('x_dtitg_notificati.notif_trans_affected');

    //[SMNOW-975] - Email notifications
    installer.RecordTransporterPackageUnpack('x_dtitg_notificati.email_footer_transla');
	//[SMNOW-1637] - Feature: Email Notifaction
    installer.RecordTransporterPackageUnpack('x_dtitg_notificati.pm_email_trans');
	installer.RecordTransporterPackageUnpack('x_dtitg_notificati.pm_email_conf');

    return installer.Finish();
}
