sys_script_include.script:
/* eslint-disable no-undef */
/* eslint-disable no-unused-vars */
/**
 * @class TicketBusinessService
 * @description Determines the business service for an incident ticket
 * @since 09/11/2023
 * @date  09/11/2023
 * @version 1.0.0
 * @author Thomas Grensemann E-Mail: <thomas.grensemann@t-systems.com>
 *
 */
class TicketBusinessService extends BusinessServiceDeterminer {

    /**
     * Determines the business service for an incident ticket
     *
     * @param {Object} current incident ticket record
     * @param {Object} sysEventEmailAction sysevent_email_action record
     * @param {Object} event event that triggers the notification
     *
     * @returns {String} service sys_id of the business service
     */
    determineBusinessService(current, sysEventEmailAction, event) {
        let service = '';

        if (current.business_service) {
            service = current.getValue('business_service');
        }

        gs.debug(`[TicketBusinessService][service] service => ${ service}`);

        return service;
    }
}
