/* eslint-disable no-unused-vars */
/**
 * @class TranslationObjectBuilder
 * @description Creates a array of translation objects.
 * @since 09/11/2023
 * @date  09/11/2023
 * @version 1.0.0
 * @author Thomas Grensemann E-Mail: <thomas.grensemann@t-systems.com>
 *
 */
class TranslationObjectBuilder {

    /**
     * Creates a array of translation objects.
     *
     * @param {String} key points to x_dtitg_notificati_translations.key
     * @param {String} emailAction sys_id of sysevent_email_action
     * @param {String} assignmentGroup business service sys_id
     * @param {String} languageId language id
     *
     * @returns {Array} transaltion objects
     */
    construct(key, emailAction, assignmentGroup, languageId) {
        const translationObject = new TranslationObject(key, false);
        const queries = QueryBuilder.findTranslation(key, emailAction, assignmentGroup, languageId);

        // eslint-disable-next-line guard-for-in
        for (const query of queries) {
            const LIMIT = 1;

            gs.debug('[Notification][TranslationObjectBuilder][construct] query ==>' + query);
            const record = x_dtitg_core_smnow.GlideRecordUtils.getLimitedGlideRecord(Constants.TRANSLATIONS, query, LIMIT);

            if (record.next()) {
                translationObject.active = Boolean(record.active);
                translationObject.translated = true;
                translationObject.languageId = languageId;
                translationObject.notification = record.notification.toString();
                translationObject.translation = record.message_html.toString();
                translationObject.assignmentGroup = record.assignment_group.toString();
                translationObject.stripHTML = Boolean(record.strip_html);

                break;
            }
        }

        gs.debug('[Notification][TranslationObjectBuilder][construct] translationObjs ==>' +
            JSON.stringify(translationObject));

        return translationObject;
    }

}
