sys_script_include.script:
/* eslint-disable no-underscore-dangle */
/* eslint-disable camelcase */
/* eslint-disable no-undef */
/* eslint-disable no-unused-vars */
/**
 * @class EventTicketAssignmentGroup
 * @description Determines the assignment group for an event ticket
 * @since 29/04/2024
 * @date  29/04/2024
 * @version 1.0.0
 * @author Thomas Grensemann E-Mail: <thomas.grensemann@t-systems.com>
 *
 */
class EventTicketAssignmentGroup extends AssignmentGroupDeterminer {

    /**
    * Determines the assignment group for an ticket event
    *
    * @param {Object} current incident ticket record
    * @param {Object} sysEventEmailAction sysevent_email_action record
    * @param {Object} event event that triggers the notification and includes in event.parm2 the assignment group sys_id
    *
    * Example:
    *   var parm2 = {
                      'assignment_group': 'AssignmentGroup sys_id',
                      'name': 'Telekom'
                    }
    *
    * gs.eventQueue('event.trigger', current, 'ABC', JSON.stringify(parm2))
    *
    * @returns {String} assignment group sys_id
    */
    determineAssignmentGroup(current, sysEventEmailAction, event) {
        let assignmentGroup = '';

        // eslint-disable-next-line no-underscore-dangle
        if (this._isEventObject(event)) {
            try {
                let obj = JSON.parse(event.parm2);
                gs.debug(`[EventTicketAssignmentGroup][determineAssignmentGroup] event => ${ JSON.stringify(event)}`);
                assignmentGroup = UtilityFunctions.findValue(obj, 'assignment_group');
                assignmentGroup = assignmentGroup ? this._determineParentGroup(assignmentGroup) : '';
            } catch (ex) {
                gs.warn(`[EventTicketAssignmentGroup][determineAssignmentGroup] JSON parsing error => ${ ex}`);
            }
        }

        gs.debug(`[EventTicketAssignmentGroup][determineAssignmentGroup] assignmentGroup => ${ assignmentGroup}`);

        return assignmentGroup;
    }

    /**
     * Checks the object event.
     *
     * @param {Object} event event object
     *
     * @returns {Boolean} true/false
    */
    _isEventObject(event) {
        return !UtilityFunctions.isObjEmpty(event) && UtilityFunctions.isEventParamTwo(event);
    }

    /**
     * Determine the assignement group.
     *
     * @param {String} assignmentGroup assignment group sys_id
     *
     * @returns {String} assignment group sys_id
     */
    _determineParentGroup(assignmentGroup) {
        let group = '';
        let record = x_dtitg_core_smnow.GlideRecordUtils.getRecord('sys_user_group', 'sys_id', assignmentGroup);

        if (record) {

            gs.debug(`[EventTicketAssignmentGroup][_determineParentGroup] assignmentGroup => ${ record.toString()}`);

            group = record.parent ? record.parent.toString() : record.toString();
        }

        return group;
    }
}
