/**
 * @class UnknownUserLink
 * @description Determines the notification to be sent for incident ticket.
 * @since 13/03/2024
 * @date  13/03/2024
 * @version 1.0.0
 * @author Siddhant Salunkhe E-Mail: <siddhant.salunkhe@t-systems.com>
 *
 */
class UnknownUserLink extends LinkGenerator {

    /**
     * Generate the ticket link
     *
	 * @param {Object} notifParams see email.engine.js
	 * @param {Object} currObj Current GlideRecord Object
     * @returns {String} ticket link
     *
     *
     */
    createTicketURL(notifParams, currObj) {
        const LIMIT = 1;
        const query = QueryBuilder.findUser(notifParams.recipients);
        let link = '';
        let gruser = x_dtitg_core_smnow.GlideRecordUtils.getLimitedGlideRecord('sys_user', query, LIMIT);

        if(gruser.next()) {
            gs.debug(`[UnknownUserLink] gruser ==> ${ JSON.stringify(gruser)}`);
            link = Constants.UNKNOW_USER;
        }

        return link;
    }
}
