sys_script_include.script:
/* eslint-disable camelcase */
/* eslint-disable no-undef */
/* eslint-disable no-unused-vars */
/**
 * @class EventTicketBusinessService
 * @description Determines the business service for an event ticket
 * @since 09/11/2023
 * @date  09/11/2023
 * @version 1.0.0
 * @author Thomas Grensemann E-Mail: <thomas.grensemann@t-systems.com>
 *
 */
class EventTicketBusinessService extends BusinessServiceDeterminer {

    /**
    * Determines the business service for an ticket event
    *
    * @param {Object} current incident ticket record
    * @param {Object} sysEventEmailAction sysevent_email_action record
    * @param {Object} event event that triggers the notification and includes in event.parm2 the company
    *
    * Example:
    *   var parm2 = {
                      'business_service': 'Business service sys_id',
                      'name': 'Telekom'
                    }
    *
    * gs.eventQueue('event.trigger', current, 'ABC', JSON.stringify(parm2))
    *
    * @returns {String} business service sys_id
    */
    determineBusinessService(current, sysEventEmailAction, event) {
        let service = '';

        // eslint-disable-next-line no-underscore-dangle
        if (this._isEventObject(event)) {
            try {
                let obj = JSON.parse(event.parm2);
                gs.debug(`[EventTicketBusinessService][determineBusinessService] event => ${ JSON.stringify(event)}`);
                service = UtilityFunctions.findValue(obj, 'business_service');
            } catch (ex) {
                gs.warn(`[EventTicketBusinessService][determineBusinessService] JSON parsing error => ${ ex}`);
            }
        }

        gs.debug(`[EventTicketBusinessService][determineBusinessService] service => ${ service}`);

        return service;
    }

    /**
     * Checks the object event.
     *
     * @param {Object} event event object
     *
     * @returns {Boolean} true/false
     */
    _isEventObject(event) {
        return !UtilityFunctions.isObjEmpty(event) && UtilityFunctions.isEventParamTwo(event);
    }
}
