/* eslint-disable no-undef */
/**
 * @class LinkGeneratorProcessor
 * @description Determines the notification(Chain of Responsibility) for the given parameters.
 * @since 11/3/2024
 * @date  11/3/2024
 * @version 1.0.0
 * @author Siddhant Salunkhe E-Mail: <siddhant.salunkhe@t-systems.com>
 *
 */

class LinkGeneratorProcessor {

    /**
     * @description This function returns exact URL of ticket
     *
     * @param {Object} notifParams see email.engine.js
     * @param {Object} currObj - Current GlideRecord Object
     * @returns{String} exact URL of ticket
     *
     */
    generateTicketURL(notifParams, currObj) {
        let link = '';

        // Chain ordering:
        // 1. UnknownUserLink
        // 2. RequesterLink
        // 3. CSDAgentLink
        // 4. FulfillerLink

        let fulfillerLink = new FulfillerLink();

        let csdLink = new ITILUserLink();
        csdLink.setNext(fulfillerLink);

        let requesterLink = new RequesterLink();
        requesterLink.setNext(csdLink);

        let unknownUserLink = new UnknownUserLink();
        unknownUserLink.setNext(requesterLink);

        // initialize chain of responsibility. Execute first chain in queue
        link = unknownUserLink.process(notifParams, currObj);

        // eslint-disable-next-line eqeqeq
        return link == Constants.UNKNOW_USER ? '' : link;
    }

}
