/**
 * @class FieldTranslator
 * @description Base class for the FieldTranslator.
 * @since 09/11/2023
 * @date  09/11/2023
 * @version 1.0.0
 * @author Thomas Grensemann E-Mail: <thomas.grensemann@t-systems.com>
 *
 */
class FieldTranslator {

    /**
     * Client passes a chain object. This improves flexibility and
     * makes testing  easier.
     *
     * @param {Object} _nextProcessor chain object
     */
    setNext(_nextProcessor) {
        this.nextProcessor = _nextProcessor;
    }

    /**
     * Runs check on the next object in chain or ends traversing if we're in
     * last object in chain.
     *
     * @param {Object} record current GlideRecord object
     * @param {String} field String with field values definitions
     * @param {String} languageId email language id
     *
     * @returns {String} translated field value
     */
    process(record, field, languageId) {
        let value = this.translateFieldValue(record, field, languageId);

        if (UtilityFunctions.isBlank(value)) {
            if (this.nextProcessor) {
                value = this.nextProcessor.process(record, field, languageId);
            }
        }

        return value;
    }

    /**
     * Translates the GlideElement objects contained in the string.
     *
     * @param {Object} record current GlideRecord object
     * @param {String} field String with field values definitions
     * @param {String} languageId email language id
     *
     */
    translateFieldValue(record, field, languageId) {
        throw new Error('Abstract method!');
    }
}
