sys_script_include.script:
/* eslint-disable no-unused-vars */
/**
 * @class TranslationObject
 * @description Constructor, Returns the translated label,
 * @since 09/11/2023
 * @date  09/11/2023
 * @version 1.0.0
 * @author Thomas Grensemann E-Mail: <thomas.grensemann@t-systems.com>
 *
 */
class TranslationObject {

    /**
     * Constructor
     *
     * @param {String} _key label key
     * @param {Boolean} _translated defined if the object contains a translation
     */
    constructor(_key, _translated) {
        this.active = false;
        this.key = _key;
        this.languageId = '';
        this.notification = '';
        this.translation = _key;
        this.assignmentGroup = '';
        this.display = true;
        this.stripHTML = false;
        this.translated = _translated;
    }


    /**
     * Returns the translated label
     *
     * @returns {String} translated label
     */
    getTranslation() {
        return this.translation;
    }

    /**
     * Returns true if the translation should be displayed
     *
     * @returns {Boolean} true/false
     */
    toDisplay() {
        return this.display;
    }

    /**
     * Returns true if the label is translated.
     *
     * @returns {Boolean} true/false
     */
    isTranslated() {
        return this.translated;
    }

    /**
     * Returns true if the html tags should be removed from the string.
     *
     * @returns {Boolean} true/false
     */
    toStrip() {
        return this.stripHTML;
    }

    /**
     * Returns an JSON string representation of the object
     * @returns {String} JSON string
     */
    toString() {
        return JSON.stringify(this);
    }
}
