sys_script_include.script:
/* eslint-disable no-unused-vars */
/* eslint-disable no-undef */
/**
 * @class CommentsService
 * @description Determines the translation for a given label key
 * @since 09/11/2023
 * @date  09/11/2023
 * @version 1.0.0
 * @author Thomas Grensemann E-Mail: <thomas.grensemann@t-systems.com>
 *
 */
class CommentsService extends ContentService {


    /**
     * Determines the translation for a given label key
     *
     * @param {Object} notifParams see email.engine.js
     */
    process(notifParams) {
        gs.debug(`[TicketLinkService] notifParams ==> ${ JSON.stringify(notifParams)}`);

        // eslint-disable-next-line no-underscore-dangle
        let content = this._getComment();
        if (content) {
            // eslint-disable-next-line no-underscore-dangle
            let incidentNumber = this._getNumber();
            this.template.print(`${gs.getMessage('incident_commented') } ${incidentNumber}: <br/><br/> ${content} <br/><br/`);
        }
    }

    /**
     * Generates the template content
     *
     *
     * @returns {String} latest comment
     */
    _getComment() {
        let content = this.current.comments.getJournalEntry(1);
        return content;
    }

    /**
     * Generates the template content
     *
     *
     * @returns {String} incident number
     */
    _getNumber() {
        let content = this.current.number;
        return content;
    }


}
