/**
 * @class RecordPacker
 * @description RecordPacker
 * @since 14/06/2024
 * @date  14/06/2024
 * @version 1.0.0
 * @author Thomas Grensemann E-Mail: <thomas.grensemann@t-systems.com>
 *
 */
class RecordPacker {

    /**
     * Record Transporter
     *
     * @param {String} table record table
     * @param {String} query encoded query
     * @param {String} name package name
     */
    pack(table, query, name) {
        const grRecords = new GlideRecord(table);
        grRecords.addEncodedQuery(query);
        grRecords.query();
        while (grRecords.next()) {
            // eslint-disable-next-line new-cap
            x_snc_devtools.RecordTransporterRecordPack(name, grRecords);
        }
    }
}
