/* eslint-disable no-undef */
/* eslint-disable no-unused-vars */
/**
 * @class GroupRecipients
 * @description Collects the recipients from the sysevent_email_action.recipient_groups.
 * @since 09/11/2023
 * @date  09/11/2023
 * @version 1.0.0
 * @author Thomas Grensemann E-Mail: <thomas.grensemann@t-systems.com>
 *
 */
class GroupRecipients extends RecipientCollector {

    /**
     * Collects the recipients from the sysevent_email_action.recipient_groups.
     *
     * @param {Object} current GlideRecord object for the ticket record
     * @param {Object} sysEventEmailAction GlideRecord object for the email notification (sysevent_email_action).
     * @param {Object} event GlideRecord object for the event that fired the notification (sysevent).
     *
     * @returns {Array} recipientList
     */
    determineRecipient(current, sysEventEmailAction, event) {
        let recipientList = [];
        let recipientGroups = sysEventEmailAction.getValue('recipient_groups');

        if (x_dtitg_core_smnow.UtilityFunctions.isNotBlank(recipientGroups)) {
            let groups = recipientGroups.split(',');
            let query = QueryBuilder.findGroupsMembers(groups);
            let record = x_dtitg_core_smnow.GlideRecordUtils.getGlideRecord(Constants.TABLE_GROUP_MEMBERS, query);

            while (record.next()) {
                recipientList.push(record.getValue('user'));
            }
        }


        gs.info(`[Notification SM.now][GroupRecipients][determineRecipient] recipientList => ${ recipientList}`);


        return recipientList;
    }
}
