/* eslint-disable new-cap */
/* eslint-disable no-undef */
/* eslint-disable camelcase */
/* eslint-disable quotes */
/**
 * @class AppInstallSetEmailScript
 * @description Inserts the global email.engine script
 * @since 29/04/2024
 * @date  29/04/2024
 * @version 1.0.0
 * @author Thomas Grensemann E-Mail: <thomas.grensemann@t-systems.com>
 *
 */
class AppInstallSetEmailScript {


    installScript(installer) {
        let grScriptEmail = new GlideRecord('sys_script_email');
        grScriptEmail.addQuery('name', 'email.engine');
        grScriptEmail.query();

        if (grScriptEmail.next()) {
            installer.RecordDelete('Removescript email', 'sys_script_email', grScriptEmail.sys_id);
        }

        grScriptEmail.initialize();
        // email.engine
        let emailscript = "var notifParams = {\n" +
        "    'assignmentGroup': '',\n" +
        "    'recipients': [],\n" +
        "    'languageId': '',\n" +
        "    'paramObj': {}\n" +
        "};\n" +
        "/**\n" +
        " * @param {Object} current Current GlideRecord\n" +
        " * @param {Object} template Handles printing to the email message\n" +
        " * @param {Object} email EmailOutbound object (null when called from Client Template)\n" +
        " * @param {Object} email_action GlideRecord for the email notification (null when called from Client Template)\n" +
        " * @param {Object} event GlideRecord for the event that fired the notification (null when called from Client Template)\n" +
        " */\n" +
        "(function runMailScript(current, template, email, email_action, event) {\n" +
        "    var assignmentGroupDeterminer = new x_dtitg_notificati.AssignmentGroupDeterminerProcessor();\n" +
        "    notifParams.assignmentGroup = assignmentGroupDeterminer.findAssignmentGroup(current, email_action, event);\n" +
        "    \n" +
        "	var recipientCollector = new x_dtitg_notificati.RecipientCollectorProcessor();\n" +
        "    notifParams.recipients = recipientCollector.collectRecipient(current, email_action, event);\n" +
        "    \n" +
        "	var emailLanguage = new x_dtitg_notificati.EmailLanguage();\n" +
        "    notifParams.languageId = emailLanguage.determineLanguageId(email_action, notifParams);\n" +
        "    \n" +
        "	var labelService = new x_dtitg_notificati.LabelService(current, template, email, email_action, event);\n" +
        "    var subject = labelService.process(x_dtitg_notificati.Constants.SUBJECT, notifParams);\n" +
        "    email.setSubject(subject);\n" +
        "}(current, template, email, email_action, event));\n" +
        "/**\n" +
        " * @param {String} service - name of service e.g. HTMLTable\n" +
        " * @param {Object} paramObj - paramObj dependent of service e.g. '{ \"name\": \"entries\", \"languageId\": \"en\" }');\n" +
        " *\n" +
        " * example:\n" +
        " * getService('HTMLTableService', '{ \"name\": \"emcos_entries\", \"languageId\": \"en\" }');\n" +
        " * getService('TranslationService', '{ \"key\": \"salutation\", \"languageId\": \"de\" }');\n" +
        " **/\n" +
        "var getService = function(service, paramObj) {\n" +
        "    notifParams.paramObj = paramObj ? JSON.parse(paramObj) : {};\n" +
        "    var contentService = x_dtitg_notificati.ContentServiceFactory\n" +
        "        .getContentService(service, current, template, email, email_action, event);\n" +
        "    contentService.process(notifParams);\n" +
        "};\n";

        installer.RecordInsertOrUpdate('Update sys_script_email', 'sys_script_email', {
            sys_scope: 'global',
            name: 'email.engine',
            script: emailscript
        });

    }
}
