    },
    RelatedRecordTableService: function(current, template, email, sysEventEmailAction, event) {
        return new RelatedRecordTableService(current, template, email, sysEventEmailAction, event);
    },
    EventNotificationService: function(current, template, email, sysEventEmailAction, event) {
        return new EventNotificationService(current, template, email, sysEventEmailAction, event);
