sys_script_include.script:
/* eslint-disable no-undef */
/* eslint-disable no-unused-vars */
/**
 * @class UserRecipients
 * @description Collects the list of users or email addresses that will receive an email notification.
 * @since 09/11/2023
 * @date  09/11/2023
 * @version 1.0.0
 * @author Thomas Grensemann E-Mail: <thomas.grensemann@t-systems.com>
 *
 */
class UserRecipients extends RecipientCollector {

    /**
     * Collects the recipient from the sysevent_email_action.recipient_users.
     *
     * @param {Object} current GlideRecord object for the ticket record
     * @param {Object} sysEventEmailAction GlideRecord object for the email notification (sysevent_email_action).
     * @param {Object} event GlideRecord object for the event that fired the notification (sysevent).
     *
     * @returns {Array} recipientList
     */
    determineRecipient(current, sysEventEmailAction, event) {
        let recipientList = [];

        let recipients = sysEventEmailAction.getValue('recipient_users');

        if (recipients) {
            let recipientArray = recipients.split(',');
            recipientList = recipientList.concat(recipientArray);
        }

        gs.info(`[Notification SM.now][UserRecipients][determineRecipient] recipientList => ${ recipientList}`);

        return recipientList;
    }
}
