/* eslint-disable no-undef */
/**
 * @class FieldTranslatorProcessor
 * @description Class that calculates (Chain of Responsibility)  the translation of a GlideElement.
 * @since 09/11/2023
 * @date  09/11/2023
 * @version 1.0.0
 * @author Thomas Grensemann E-Mail: <thomas.grensemann@t-systems.com>
 *
 */
class FieldTranslatorProcessor {

    /**
     * Function that calculates (Chain of Responsibility) the
     * the translation of a GlideElement.
     *
     * @param {Object} record GliedRecord object
     * @param {String} field Column name for which to return the GlideElement object.
     * @param {String} languageId email language id
     *
     * @returns {String} value teh ransalted field label or the refernce display value
     *
     */
    translate(record, field, languageId) {
        let value = '';

        let valueFieldTranslator = new ValueFieldTranslator();

        let textFieldTranslator = new TextFieldTranslator();
        textFieldTranslator.setNext(valueFieldTranslator);

        let choiceFieldTranslator = new ChoiceFieldTranslator();
        choiceFieldTranslator.setNext(textFieldTranslator);

        let referenceFieldTranslator = new ReferenceFieldTranslator();
        referenceFieldTranslator.setNext(choiceFieldTranslator);

        value = referenceFieldTranslator.process(record, field, languageId);

        return value;
    }
}
