/* eslint-disable no-undef */
/* eslint-disable no-unused-vars */
/**
 * @class RecipientCollectorProcessor
 * @description Collects the email recipients.
 * @since 09/11/2023
 * @date  09/11/2023
 * @version 1.0.0
 * @author Thomas Grensemann E-Mail: <thomas.grensemann@t-systems.com>
 *
 */
class RecipientCollectorProcessor {

    /**
     * Function that collects the email recipients.
     *
     * @param {Object} current GlideRecord object for the ticket record
     * @param {Object} sysEventEmailAction GlideRecord object for the email notification (sysevent_email_action).
     * @param {Object} event GlideRecord object for the event that fired the notification (sysevent).
     *
     * @returns {Array} recipientList
     */
    collectRecipient(current, sysEventEmailAction, event) {
        let recipientList = [];

        let userRecipients = new UserRecipients().determineRecipient(current, sysEventEmailAction, event);
        let fieldRecipients = new FieldRecipients().determineRecipient(current, sysEventEmailAction, event);
        let groupRecipients = new GroupRecipients().determineRecipient(current, sysEventEmailAction, event);
        let eventRecipients = new EventRecipients().determineRecipient(current, sysEventEmailAction, event);
        recipientList = recipientList.concat(userRecipients, fieldRecipients, groupRecipients, eventRecipients);

        return recipientList;
    }
}
