sys_script_include.script:
/* eslint-disable no-undef */
/* eslint-disable no-unused-vars */
/**
 * @class ContentServiceFactory
 * @description Creational pattern script for the ContentServices
 * @since 09/11/2023
 * @date  09/11/2023
 * @version 1.0.0
 * @author Thomas Grensemann E-Mail: <thomas.grensemann@t-systems.com>
 *
 */
class ContentServiceFactory {

    /**
     * Factory function
     *
     * @param {String} service content service that should be executed
     * @param {Object} current GlideRecord object for the ticket record
     * @param {Object} template Handles printing from the mail script to the email message.
     * @param {Object} email EmailOutbound object
     * @param {Object} sysEventEmailAction GlideRecord object for the email notification (sysevent_email_action).
     * @param {Object} event GlideRecord object for the event that fired the notification (sysevent).
     *
     * @returns {Object} contentService
     */
    static getContentService(service, current, template, email, sysEventEmailAction, event) {
        let serviceObj = null;

        gs.debug(`[Notification][ContentServiceFactory] [getContentService] service ==> ${ service}`);

        if (ContentServices.hasOwnProperty(service)) {
            serviceObj = ContentServices[service](current, template, email, sysEventEmailAction, event);
        }

        return serviceObj;
    }
}
