/* eslint-disable no-unused-vars */
/* eslint-disable no-undef */
/**
 * @class ExpressionCondition
 * @description Evaluates a given exprension.
 * @since 09/11/2023
 * @date  09/11/2023
 * @version 1.0.0
 * @author Thomas Grensemann E-Mail: <thomas.grensemann@t-systems.com>
 *
 */
class ExpressionCondition extends ConditionController {

    /**
     * Evaluates a given exprension.
     *
     * @param {object} conf see NotificationConditionController#notificationEnabled()
     *
     * @returns {Boolean} true/false
     */
    checkCondition(conf) {
        let evaluation = true;
        let contains = 'expression' in conf;

        if (contains) {
            evaluation = conf.expression;
        }

        gs.debug(`[ExpressionCondition][validateNotification] answer => ${ evaluation}`);

        return evaluation;
    }
}
