sys_script_include.script:
/* eslint-disable camelcase */
/* eslint-disable no-unused-vars */
/* eslint-disable no-undef */
/**
 * @class FieldLabelObjectBuilder
 * @description Generates the filed label array
 * @since 09/11/2023
 * @date  09/11/2023
 * @version 1.0.0
 * @author Thomas Grensemann E-Mail: <thomas.grensemann@t-systems.com>
 *
 */
class FieldLabelObjectBuilder {

    /**
     * Generates the filed label array
     *
     * @param {String} table table name
     * @param {Array} fieldList field list
     * @param {String} languageId language id
     * @returns {Array} fieldLabels
     */
    construct(table, fieldList, languageId) {
        let fieldLabels = [];
        let parent = GlideRecordUtils.getParentTable(table);

        gs.debug(`[FieldLabelObjectBuilder][construct] parent => ${ parent}`);

        let queries = QueryBuilder.findTranslatedFieldQuery(table, parent, fieldList, languageId);

        // eslint-disable-next-line guard-for-in
        for (const query of queries) {
            gs.debug(`[FieldLabelObjectBuilder][construct] query => ${ query}`);
            let record = x_dtitg_core_smnow.GlideRecordUtils.getGlideRecord(Constants.SYS_DOCUMENTATION, query);

            if (record.hasNext()) {
                while (record.next()) {
                    let label = {};
                    label.table = record.name.toString();
                    label.parent = parent;
                    label.element = record.element.toString();
                    label.language = record.language.toString();
                    label.label = record.label.toString();

                    fieldLabels.push(label);
                }
                break;
            }

        }

        return fieldLabels;
    }
}
