sys_script_include.script:
/* eslint-disable camelcase */
/* eslint-disable no-undef */
/* eslint-disable no-unused-vars */
/**
 * @class OptionSchemaReader
 * @description Read an options schema for a notification.
 * @since 13/06/2024
 * @date  13/06/2024
 * @version 1.0.0
 * @author Thomas Grensemann E-Mail: <thomas.grensemann@t-systems.com>
 *
 */
class OptionSchemaReader {

    /**
     * Finds an object in an array of objects by value of property
     *
     * @param {String} tag email tag name
     * @param {Object} sysEventEmailAction  GlideRecord for the email notification (null when called from Client Template)
     *
     * @returns {Object} obj javascript object
     */
    readOptions(tag, sysEventEmailAction) {
        const LIMIT = 1;
        let options = {};
        const query = QueryBuilder.findEntries('notification', sysEventEmailAction.sys_id);
        let record = x_dtitg_core_smnow.GlideRecordUtils.getLimitedGlideRecord(Constants.CONFIGURATION, query, LIMIT);

        if(record.next()) {
            let optionSchema = UtilityFunctions.parse(record.getValue('option_schema'));
            options = UtilityFunctions.findObj(optionSchema, 'tag', tag);
        }

        return options;
    }

}
