/* eslint-disable no-undef */
/* eslint-disable no-unused-vars */
/**
 * @class ProblemTicketCompany
 * @description Determines the company for an problem ticket.
 * @since 09/11/2023
 * @date  09/11/2023
 * @version 1.0.0
 * @author Thomas Grensemann E-Mail: <thomas.grensemann@t-systems.com>
 *
 */
class ProblemTicketCompany extends CompanyDeterminer {

    /**
     * Determines the company for an problem ticket
     *
     * @param {Object} current problem ticket record
     * @param {Object} sysEventEmailAction sysevent_email_action record
     * @param {Object} event event that triggers the notificatoin
     *
     * @returns {String} company sys_id
     */
    determineCompany(current, sysEventEmailAction, event) {
        let company = '';

        try {
            if (current.getTableName() === Constants.PROBLEM) {
                company = current.opened_by.company.toString();
            }
        } catch (ex) {
            throw new CompanyNotFoundException(`[ProblemTicketCompany][determineCompany] ... ==> ${ ex.message}`);
        }

        gs.debug(`[ProblemTicketCompany][determineCompany] company => ${ company}`);

        return company;
    }
}
