sys_script_include.script:
/* eslint-disable no-undef */
/* eslint-disable no-unused-vars */
/**
 * @class TranslationService
 * @description Determines the translation for a given label key
 * @since 09/11/2023
 * @date  09/11/2023
 * @version 1.0.0
 * @author Thomas Grensemann E-Mail: <thomas.grensemann@t-systems.com>
 *
 */
class TranslationService extends ContentService {

    /**
     * Determines the translation for a given label key
     *
     * get('TranslationService', '{ "key": "number", "languageId": "de" }');
     * get('TranslationService', '{ "key": "number"}');
     *
     * var notifParams = {
          'businessService': '',
          'recipients': '',
          'languageId': '',
          'paramObj': '';
        };
     *
     * @param {Object} notifParams see email.engine.js
     */
    process(notifParams) {
        gs.debug(`[TranslationService] notifParams ==> ${ JSON.stringify(notifParams)}`);

        let languageId = this.getLanguageId(notifParams);
        gs.debug(`[TranslationService][process] sysEventEmailAction name =>${ this.sysEventEmailAction.name }  languageId =>${ languageId }`);

        let key = this.getLabelKey(notifParams);
        let assignmentGroup = this.getParameter(notifParams, 'assignmentGroup');
        let emailAction = this.sysEventEmailAction.getValue('sys_id');

        gs.debug(`[Notification][TranslationService][process] key => ${ key } languageId => ${ languageId
        } assignmentGroup => ${ assignmentGroup } emailAction  => ${ emailAction}`);

        let translationObj = new TranslationObjectBuilder().construct(key, emailAction, assignmentGroup, languageId);

        if (translationObj.toDisplay()) {
            let translation = new TranslationProcessor().process(translationObj, this.current);
            translation = new GlideElementDecoder().decode(this.current, translation, languageId);
            this.template.print(translation);
        }
    }
}
