/* eslint-disable no-unused-vars */
/* eslint-disable no-undef */
/**
 * @class ReferenceFieldTranslator
 * @description Translates the field values of field type reference.
 * @since 09/11/2023
 * @date  09/11/2023
 * @version 1.0.0
 * @author Thomas Grensemann E-Mail: <thomas.grensemann@t-systems.com>
 *
 */
class ReferenceFieldTranslator extends FieldTranslator {

    /**
     * Translates the field values of field type reference.
     *
     * @param {Object} record GliedRecord object
     * @param {String} field Column name for which to return the GlideElement object.
     * @param {String} languageId email language id
     *
     * @returns {String} value
     *
     */
    translateFieldValue(record, field, languageId) {
        const types = Constants.INTERNAL_TYPES;
        const limit = 1;
        let value = '';
        let internalType = GlideRecordUtils.getInternalType(record, field);

        gs.debug(`[Notifications SM.Now][ReferenceFieldTranslator][translateFieldValue] field ==> ${field}`);
        gs.debug(`[Notifications SM.Now][ReferenceFieldTranslator][translateFieldValue] internalType ==> ${internalType}`);

        if (internalType === types.REFERENCE) {
            value = GlideRecordUtils.getElementDisplayValue(record, field);
        }

        gs.debug(`[Notifications SM.Now][ReferenceFieldTranslator][translateFieldValue] value ==> ${value}`);

        return value;
    }
}
