sys_script_include.script:
/* eslint-disable camelcase */
/* eslint-disable no-undef */
/* eslint-disable no-unused-vars */

/**
 * @class HtmlEmailGenerator
 * @description Generates a HTML email for the given data.
 * @since 09/11/2023
 * @date  09/11/2023
 * @version 1.0.0
 * @author Thomas Grensemann E-Mail: <thomas.grensemann@t-systems.com>
 *
 */
class HtmlEmailGenerator {

    /**
     * Generates a HTML Email for the given data.
     *
     * @param {JSON} data email content

     * @returns {String} HTML email
     */
    writeEmail(data) {

        let mailHeader = this.writeEmailHeader(data);
        let mailBody = this.writeEmailBody(data);
        let mailLink = this.writeEmailLink(data);
        let mailFooter = this.writeEmailFooter(data);

        let builder = new x_dtitg_core_smnow.StringBuilder();

        builder.append(mailHeader);
        builder.append(mailBody);
        builder.append(mailLink);
        builder.append(mailFooter);

        return builder.toString();
    }

    /**
     * Generates a HTML Email header for the given data.
     *
     * @param {JSON} data email content

     * @returns {String} HTML email header
     */
    writeEmailHeader(data) {
        let header = UtilityFunctions.findValue(data, Constants.EMAIL_HEADER);
        let builder = new x_dtitg_core_smnow.StringBuilder();

        builder.append('<p>');
        builder.append(header);
        builder.append('</p>');

        return builder.toString();
    }

    /**
     * Generates a HTML Email body for the given data.
     *
     * @param {JSON} data email content

     * @returns {String} HTML email body
     */
    writeEmailBody(data) {
        let emailBody = UtilityFunctions.findValue(data, Constants.EMAIL_BODY);
        let builder = new x_dtitg_core_smnow.StringBuilder();

        builder.append('<p>');
        builder.append(emailBody);
        builder.append('</p>');


        return builder.toString();
    }

    /**
     * Generates a HTML link for the given data.
     *
     * @param {JSON} data email content

     * @returns {String} HTML email link
     */
    writeEmailLink(data) {
        let emailLink = '';
        let url = UtilityFunctions.findValue(data, Constants.EMAIL_LINK_URL);
        let name = UtilityFunctions.findValue(data, Constants.EMAIL_LINK_NAME);

        if(UtilityFunctions.isNotBlank(name)) {

            let builder = new x_dtitg_core_smnow.StringBuilder();
            builder.append('<p>');
            builder.append('<a href="');
            builder.append(url);
            builder.append('">');
            builder.append(name);
            builder.append('</a>');
            builder.append('</p>');

            emailLink = builder.toString();
        }

        return emailLink;
    }

    /**
     * Generates a HTML Email footer for the given data.
     *
     * @param {JSON} data email content

     * @returns {String} HTML email footer
     */
    writeEmailFooter(data) {
        let emailFooter = '';
        let footer = UtilityFunctions.findValue(data, Constants.EMAIL_FOOTER);
        if(UtilityFunctions.isNotBlank(footer)) {
            let builder = new x_dtitg_core_smnow.StringBuilder();

            builder.append('<p>');
            builder.append(footer);
            builder.append('</p>');

            emailFooter = builder.toString();
        }

        return emailFooter;
    }
}
