sys_script_include.script:
/* eslint-disable no-undef */
/* eslint-disable no-unused-vars */
/**
 * @class FieldRecipients
 * @description Collects the recipients from the sysevent_email_action.recipient_fields.
 * @since 09/11/2023
 * @date  09/11/2023
 * @version 1.0.0
 * @author Thomas Grensemann E-Mail: <thomas.grensemann@t-systems.com>
 *
 */
class FieldRecipients extends RecipientCollector {

    /**
     * Collects the recipients from the sysevent_email_action.recipient_fields.
     *
     * @param {Object} current GlideRecord object for the ticket record
     * @param {Object} sysEventEmailAction GlideRecord object for the email notification (sysevent_email_action).
     * @param {Object} event GlideRecord object for the event that fired the notification (sysevent).
     *
     * @returns {Array} recipientList
     */
    determineRecipient(current, sysEventEmailAction, event) {
        let recipientList = [];
        let recipientFields = sysEventEmailAction.getValue('recipient_fields');

        if (recipientFields) {
            gs.debug(`[Notifications SM.Now][FieldRecipientsListBuilder][construct] recipientFields ==> ${ recipientFields}`);

            let recipients = recipientFields.split(',');

            // eslint-disable-next-line guard-for-in
            for (let index in recipients) {
                let field = recipients[index];
                let recipient = GlideRecordUtils.getElement(current, field);

                recipientList.push(recipient);
            }
        }


        gs.info(`[Notification SM.now][FieldRecipients][determineRecipient] recipientList => ${ recipientList}`);


        return recipientList;
    }
}
