sys_script_include.script:
/* eslint-disable no-undef */
/* eslint-disable no-unused-vars */
/**
 * @class LabelService
 * @description Determines the translation for a given label key
 * @since 09/11/2023
 * @date  09/11/2023
 * @version 1.0.0
 * @author Thomas Grensemann E-Mail: <thomas.grensemann@t-systems.com>
 *
 */
class LabelService extends ContentService {

    /**
    * Determines the translation for a given label key
    *
    * @param {String} key points to x_dtitg_notificati_translations.key
    * @param {Object} notifParams global object: see email.engine.js
    *
    * var notifParams = {
        'businessService': '',
        'recipients': '',
        'languageId': '',
        'paramObj': '';
      };
    *
    *
    * @returns {String} translation the translated label
    */
    process(key, notifParams) {

        gs.debug(`[LabelService] key ==> ${ key}`);
        gs.debug(`[LabelService] notifParams ==> ${ JSON.stringify(notifParams)}`);
        let languageId = this.getLanguageId(notifParams);
        let assignmentGroup = this.getParameter(notifParams, 'assignmentGroup');

        let emailAction = this.sysEventEmailAction.getValue('sys_id');
        let translationObj = new TranslationObjectBuilder().construct(key, emailAction, assignmentGroup, languageId);
        let translation = new TranslationProcessor().process(translationObj);

        gs.debug(`[LabelService] translation ==> ${ translation}`);

        if (this.current) {
            translation = new GlideElementDecoder().decode(this.current, translation, languageId);
        }

        return translation;
    }
}
