sys_script_include.script:
/* eslint-disable no-undef */
/* eslint-disable no-unused-vars */
/**
 * @class IncidentTicketCompany
 * @description Determines the company for an incident ticket.
 * @since 09/11/2023
 * @date  09/11/2023
 * @version 1.0.0
 * @author Thomas Grensemann E-Mail: <thomas.grensemann@t-systems.com>
 *
 */
class IncidentTicketCompany extends CompanyDeterminer {

    /**
     * Determines the company for an incident ticket
     *
     * @param {Object} current incident ticket record
     * @param {Object} sysEventEmailAction sysevent_email_action record
     * @param {Object} event event that triggers the notification
     *
     * @returns {String} company sys_id
     */
    determineCompany(current, sysEventEmailAction, event) {
        let company = '';

        try {
            if (current.getTableName() === Constants.INCIDENT) {
                company = current.x_dtitg_itsm_smnow_affected_user.company.toString();
            }
        } catch (ex) {
            throw new CompanyNotFoundException(`[IncidentTicketCompany][determineCompany] ... ==> ${ ex.message}`);
        }
        gs.debug(`[IncidentTicketCompany][determineCompany] company => ${ company}`);

        return company;
    }
}
