sys_script_include.script:
/* eslint-disable no-undef */
/* eslint-disable no-unused-vars */
/**
 * @class GlideElementDecoder
 * @description Decodes the GlideElement objects contained in the string.
 * @since 09/11/2023
 * @date  09/11/2023
 * @version 1.0.0
 * @author Thomas Grensemann E-Mail: <thomas.grensemann@t-systems.com>
 *
 */
class GlideElementDecoder {

    /**
     * Decodes the GlideElement objects contained in the string.
     *
     * @param {Object} record GliedRecord object
     * @param {String} string String with field values definitions
     * @param {String} languageId email language id
     *
     *
     * @returns {String} translated string
     */
    decode(record, string, languageId) {
        // eslint-disable-next-line no-var
        let decodedString = string;
        let processor = new FieldTranslatorProcessor();
        let match = '';
        const index = 1;
        const firstMatch = 0;
        const regex = /\$\{(.*?)\}/gm;

        do {

            match = regex.exec(string);
            gs.debug(`[GlideElementDecoder][decode] match ==> ${ match}`);
            if (match) {
                let field = match[index];

                let value = processor.translate(record, field, languageId);
                let toReplace = match[firstMatch];

                gs.debug(`[GlideElementDecoder][decode] field ==> ${ field} : value ==> ${ value} : toReplace  ==> ${ toReplace}`);

                decodedString = decodedString.replace(toReplace, value);
            }
        } while (match);

        gs.debug(`[GlideElementDecoder][decode] decodedString ==> ${ decodedString}`);

        return decodedString;
    }
}
