/* eslint-disable no-undef */
/* eslint-disable no-unused-vars */
/**
 * @class TranslationProcessor
 * @description Proceesd the current TranslationObject.
 * @since 09/11/2023
 * @date  09/11/2023
 * @version 1.0.0
 * @author Thomas Grensemann E-Mail: <thomas.grensemann@t-systems.com>
 *
 */
class TranslationProcessor {

    /**
     * Proceesd the current TranslationObject.
     *
     * @param {Object} translationObj see TranslationObject
     * @param {Object} current current GlideRecord object
     *
     * @returns {String} translation the proceed translation
     */
    process(translationObj) {
        let translation = translationObj.getTranslation();

        if (translationObj.isTranslated()) {
            translation = TranslationDecoder.entityDecode(translation);
            translation = TranslationDecoder.namedEntityDecode(translation);
            translation = TranslationDecoder.stripHtml(translationObj, translation);
        }

        return translation;
    }
}
