sys_script_include.script:
/* eslint-disable no-undef */
/* eslint-disable no-unused-vars */
/**
 * @class TextFieldTranslator
 * @description Translates the field values of field text and html
 * @since 09/11/2023
 * @date  09/11/2023
 * @version 1.0.0
 * @author Thomas Grensemann E-Mail: <thomas.grensemann@t-systems.com>
 *
 */
class TextFieldTranslator extends FieldTranslator {

    /**
     * Translates the field values of field text and html
     *
     * @param {Object} record GliedRecord object
     * @param {String} field Column name for which to return the GlideElement object.
     * @param {String} languageId email language id
     *
     * @returns {String} value
     *
     */
    translateFieldValue(record, field, languageId) {
        const types = Constants.INTERNAL_TYPES;
        const limit = 1;
        let value = '';
        let internalType = GlideRecordUtils.getInternalType(record, field);

        gs.debug(`[Notifications SM.Now][TextFieldTranslator][translateFieldValue] internalType ==> ${internalType}`);

        if (internalType === types.TRANSLATED_TEXT || internalType === types.TRANSLATED_HTML) {
            let name = UtilityFunctions.determineTable(record);
            let documentKey = record.getValue('sys_id');
            let queries = QueryBuilder.findTextTranslation(documentKey, name, field, languageId);

            // eslint-disable-next-line guard-for-in
            for (const query of queries) {
                // eslint-disable-next-line camelcase
                let gr = x_dtitg_core_smnow.GlideRecordUtils.getLimitedGlideRecord(Constants.SYS_TRANSLATED_TEXT, query, limit);
                if (gr.next()) {
                    value = gr.getValue('value');

                    break;
                }
            }
        }

        return value;
    }
}
