sys_script_include.script:
/* eslint-disable no-unused-vars */
/**
 * @class TableRecord
 * @description Adds a fieldRecord object to the fieldRecords array.
 * @since 09/11/2023
 * @date  09/11/2023
 * @version 1.0.0
 * @author Thomas Grensemann E-Mail: <thomas.grensemann@t-systems.com>
 *
 */
class TableRecord {

    /**
     * Constructor
     */
    constructor() {
        this.fieldRecords = [];
    }

    /**
     * Adds a fieldRecord object to the fieldRecords array.
     *
     * @param {Object} _fieldRecord fieldRecord object
     */
    addFieldRecord(_fieldRecord) {
        this.fieldRecords.push(_fieldRecord);
    }

    /**
     * Returns the fieldRecords
     *
     * @returns {Array} fieldRecords
     */
    getFieldRecords() {
        return this.fieldRecords;
    }

    /**
     * Returns an JSON string representation of the object
     * @returns {String} JSON string
     */
    toString() {
        return JSON.stringify(this);
    }
}
